    public void tampil_data(){
    DefaultTableModel tabel=new DefaultTableModel();
    tabel.addColumn("ID");
    tabel.addColumn("Merek");
    tabel.addColumn("Status"); //menampilkan title dari tabel
    tabel.addColumn("Harga");
    
    try {
        java.sql.Connection conn=(java.sql.Connection)aplikasipenyewaanpc.Koneksi.koneksiDB();
        String sql = "select * from tambahpc"; //koneksi ke databas dan menampilkan data di tabel
        java.sql.PreparedStatement pst = conn.prepareStatement(sql);
        ResultSet rs = pst.executeQuery(sql);
      while(rs.next())
        {
        tabel.addRow(new Object[]{
            rs.getString(1),
            rs.getString(2),  //menampilkan data sesuai di tabel
            rs.getString(3),
            rs.getString(4)});
        }
        jTable1.setModel(tabel);
        }
    catch (Exception e){
    }
}

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
      try{
            String sql="insert into tambahpc values('"
                    +idPC.getText()+"','"
                    +tMerek.getText()+"','" //menambahkan data di database dan pada tabel 
                    +jStatus.getSelectedItem()+"','"
                    +tHarga.getText()+"')";
                 java.sql.Connection conn=(java.sql.Connection)aplikasipenyewaanpc.Koneksi.koneksiDB();
            java.sql.PreparedStatement pst=conn.prepareStatement(sql); //untuk koneksi my sql
            pst.execute();
            JOptionPane.showMessageDialog(null, "Berhasil disimpan");
            tampil_data();
        }
        catch (Exception e){
            JOptionPane.showMessageDialog(null, "Gagal disimpan");
            System.out.println(e.getMessage());
        }
      idPC.setText("");
      tMerek.setText(""); //mengosongkan field ketika tambah data berhasil
      tHarga.setText("");
     

        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

