    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
          try{
            String sql="update tambahpc set merek= '" + tMerek.getText()+
                    "', status='" + jStatus.getSelectedItem()+
                    "', harga='" + tHarga.getText()+     //koding untuk melakukan edit data 
                    "' where id='" + idPC.getText()+"'";
            java.sql.Connection conn=(java.sql.Connection)aplikasipenyewaanpc.Koneksi.koneksiDB();
            java.sql.PreparedStatement pst=conn.prepareStatement(sql); //untuk koneksi ke mysql
            pst.execute();
            JOptionPane.showMessageDialog(null, "Berhasil diedit");
            tampil_data();
        }
        catch (Exception e){
            JOptionPane.showMessageDialog(null, "Gagal edit");
            System.out.println(e.getMessage());
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
          try{
    java.sql.Connection conn=(java.sql.Connection)aplikasipenyewaanpc.Koneksi.koneksiDB();
    String sql = "delete from tambahpc where id='"+idPC.getText()+"' "; //untuk menghapus data di mysql dan tabel
    java.sql.PreparedStatement pst = conn.prepareStatement(sql);
    pst.executeUpdate(); JOptionPane.showMessageDialog(null,"Data berhasil di hapus");
    tampil_data();
    }
catch (Exception e){
JOptionPane.showMessageDialog(null,"Proses Penghapusan Gagal");
System.out.println(e.getMessage());
}        // TODO add your handling code here:
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MouseClicked
       DefaultTableModel tab = (DefaultTableModel)jTable1.getModel();
       idPC.setText(tab.getValueAt(jTable1.getSelectedRow(), 0).toString()); 
       tMerek.setText(tab.getValueAt(jTable1.getSelectedRow(), 1).toString());
       tHarga.setText(tab.getValueAt(jTable1.getSelectedRow(), 3).toString());
       //ketika diklik akan menampilkan data yang diklik
    }//GEN-LAST:event_jTable1MouseClicked

